    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        int num = Integer.valueOf(jTextFieldNumero.getText());
        int resto=0;
        int divisor=1;
        while(divisor<=num){
             if (num%divisor==0){
                 resto=resto+1;  
            }
             divisor = divisor+1;
        }if(resto==2){
            jTextArea.append("El numero "+num+"es primo\n");
        }else{
            jTextArea.append("El numero "+num+" no es primo\n");
        }
    }//GEN-LAST:event_jButton1ActionPerformed

